import Slider from "react-slick";

export default function Experience(props){
    let experince=props.content;
    const settings = {
        dots: false,
        infinite: true,
        speed: 500,
        autoplay: true,
        autoplaySpeed: 2000,
        slidesToShow: 1 ,
        arrows: false,
      };
    return(
        <Slider {...settings}>
        {
            experince.map((el,index)=>(
                <div key={index}>
                    <div className="flex gap-5 items-start" style={{'display':'flex !important'}}>
                        <div className="flex-1">
                            <img src={el.companyLogo}></img>
                        </div>
                        <div className="flex-[2]">
                            <p className="font-bold text-2xl">{el.name}</p>
                            <p className="text-slate-600">{el.position}</p>
                            <p className="text-xs">{el.startDate}&nbsp;-&nbsp;{!el.endDate? 'Present':el.endDate}</p>
                        </div>
                        <p className="flex-[3]" dangerouslySetInnerHTML={{__html:el.description}}></p>
                    </div>
                </div>
            ))
        }
        </Slider>
    )
}
