import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPhone, faEnvelope } from "@fortawesome/free-solid-svg-icons";

export default function Header(){
    const navArray=[
        {
            title:'Profile',
            id:'profile',
            route:'/profile'
        },
        {
            title:'About',
            id:'about',
            route:'/about'
        },
        {
            title:'Contact Us',
            id:'contact',
            route:'/contactUs'
        },
    ]
    const contactDetail=[
        {
            title:'kiran.panchal@benepik.com',
            id:'2',
            route:'',
            icon:faEnvelope,
        },
        {
            title:'+91 9355111396',
            id:'1',
            route:'',
            icon:faPhone,
        },
    ]
    return(
        <>
            <div className="contactDetail bg-indigo-950 py-2 px-4">
                <ContactDetails detail={contactDetail}></ContactDetails>
            </div>
            <div className="flex justify-between items-center bg-white shadow-lg flex-col sm:flex-row px-4">
                <img src="/images/logo.png" className="w-40"></img>
                <div>
                    <ul className="flex gap-4 *:font-bold *:text-base">
                        <Navbar content={navArray}></Navbar>
                    </ul>
                </div>
            </div>
        </>
    )
}

export function Navbar(props){
    const list=props.content;
    return(
        <>
        {
            list.map((el,index)=>(
                <li key={el.id}><p>{el.route=='' && <span className="me-3"><FontAwesomeIcon icon={el.icon} /></span>}{el.title}{(el.route==''&&list.length-1!=index)&&<span className="mx-3 hidden md:inline">|</span>}</p></li>
            ))
        }
        </>
    )
}

export function ContactDetails(props){

    return(
        <div className="flex justify-between text-white flex-col sm:flex-row">
            <h1 className="text-xl font-bold">Lorem Ipsum</h1>
            <div className="contact">
                <ul className="flex flex-col sm:flex-row">
                    <Navbar content={props.detail}></Navbar>
                </ul>
            </div>
        </div>
    )
}
