import Slider from "react-slick";
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";

export default function Experience(props){
    let experince=props.content;
    const settings = {
        dots: false,
        infinite: false,
        speed: 500,
        autoplay: false,
        autoplaySpeed: 2000,
        slidesToShow: 1 ,
        arrows: true,
      };
    return(
        // {'width':'90%','display':'block','margin':'auto'}
        <Slider {...settings}>
        {
            experince.map((el,index)=>(
                // <div  className="!block ">
                    <div key={index} className="!flex gap-5 items-start !w-5/6 m-auto">
                        <div className="flex-[2]">
                            <img src={el.companyLogo}></img>
                            <p className="font-bold text-2xl">{el.name}</p>
                            <p className="text-slate-600">{el.position}</p>
                            <p className="text-xs">{el.startDate}&nbsp;-&nbsp;{!el.endDate? 'Present':el.endDate}</p>
                        </div>
                        <p className="flex-[3] fadeIn" dangerouslySetInnerHTML={{__html:el.description}}></p>
                    </div>
                // </div> 
            ))
        }
        </Slider>
    )
}
